using System.Windows.Forms;
using System.Drawing;
using System.Drawing.Drawing2D;
public partial class ToggleSwitch : CheckBox { public ToggleSwitch() { this.MinimumSize = new Size(45, 22); SetStyle(ControlStyles.UserPaint | ControlStyles.AllPaintingInWmPaint, true); } private GraphicsPath GetFigurePath() { int arcSize = this.Height - 1; Rectangle leftArc = new Rectangle(0, 0, arcSize, arcSize); Rectangle rightArc = new Rectangle(this.Width - arcSize - 2, 0, arcSize, arcSize); GraphicsPath path = new GraphicsPath(); path.StartFigure(); path.AddArc(leftArc, 90, 180); path.AddArc(rightArc, 270, 180); path.CloseFigure(); return path; } protected override void OnPaint(PaintEventArgs pevent) { int toggleSize = this.Height - 5; pevent.Graphics.SmoothingMode = SmoothingMode.AntiAlias; pevent.Graphics.Clear(this.Parent.BackColor); if (this.Checked) { pevent.Graphics.FillPath(new SolidBrush(Color.FromArgb(0, 120, 215)), GetFigurePath()); pevent.Graphics.FillEllipse(new SolidBrush(Color.WhiteSmoke), new Rectangle(this.Width - this.Height + 1, 2, toggleSize, toggleSize)); } else { pevent.Graphics.FillPath(new SolidBrush(Color.FromArgb(90, 90, 90)), GetFigurePath()); pevent.Graphics.FillEllipse(new SolidBrush(Color.Gainsboro), new Rectangle(2, 2, toggleSize, toggleSize)); } } }